import type { Firework, Particle } from '@/types'
import { calculateTextOpacity, generateTextFireworkConfig, getTextMetrics } from '@/utils/textFireworks'

/**
 * åˆå§‹åŒ– Canvas ä¸Šä¸‹æ–‡
 * è®¾ç½®é«˜åˆ†å±æ”¯æŒ (DPR)
 * 
 * @param canvas Canvas å…ƒç´ 
 * @param width é€»è¾‘å®½åº¦
 * @param height é€»è¾‘é«˜åº¦
 * @returns Canvas ä¸Šä¸‹æ–‡å¯¹è±¡
 */
export function initCanvasContext(
  canvas: HTMLCanvasElement,
  width: number,
  height: number
): {
  ctx: CanvasRenderingContext2D
  dpr: number
  width: number
  height: number
} {
  const dpr = window.devicePixelRatio || 1
  const ctx = canvas.getContext('2d')!

  // è®¾ç½® Canvas ç‰©ç†å°ºå¯¸
  canvas.width = width * dpr
  canvas.height = height * dpr

  // è®¾ç½® Canvas é€»è¾‘å°ºå¯¸
  canvas.style.width = `${width}px`
  canvas.style.height = `${height}px`

  // ç¼©æ”¾ä¸Šä¸‹æ–‡ä»¥é€‚åº”é«˜åˆ†å±
  ctx.scale(dpr, dpr)

  return { ctx, dpr, width, height }
}

/**
 * æ¸…ç©º Canvas èƒŒæ™¯
 * ä½¿ç”¨æ·±å¤œæ¸å˜æ•ˆæœ
 * 
 * @param ctx Canvas ä¸Šä¸‹æ–‡
 * @param width Canvas å®½åº¦
 * @param height Canvas é«˜åº¦
 */
export function clearCanvas(
  ctx: CanvasRenderingContext2D,
  width: number,
  height: number
): void {
  // åˆ›å»ºå¾„å‘æ¸å˜ (æ·±è“é»‘åˆ°çº¯é»‘)
  const gradient = ctx.createRadialGradient(
    width / 2,
    height / 2,
    0,
    width / 2,
    height / 2,
    Math.max(width, height)
  )

  gradient.addColorStop(0, '#0a0e27')  // ä¸­å¿ƒï¼šæ·±è“é»‘
  gradient.addColorStop(0.5, '#0f1419')  // ä¸­é—´è¿‡æ¸¡
  gradient.addColorStop(1, '#000000')  // å¤–åœˆï¼šçº¯é»‘

  ctx.fillStyle = gradient
  ctx.fillRect(0, 0, width, height)

  // å¯é€‰ï¼šæ·»åŠ æ˜Ÿè¾°è£…é¥°
  drawStars(ctx, width, height)
}

/**
 * ç»˜åˆ¶æ˜Ÿè¾°èƒŒæ™¯
 * ä½¿ç”¨ä¼ªéšæœºç®—æ³•ï¼Œç¡®ä¿æ¯æ¬¡åŠ è½½ä½ç½®ä¸€è‡´
 * 
 * @param ctx Canvas ä¸Šä¸‹æ–‡
 * @param width Canvas å®½åº¦
 * @param height Canvas é«˜åº¦
 */
function drawStars(ctx: CanvasRenderingContext2D, width: number, height: number): void {
  // ä¼ªéšæœºæ•°ç”Ÿæˆå™¨ (ä½¿ç”¨å›ºå®šç§å­ï¼Œæ¯æ¬¡åŠ è½½æ˜Ÿè¾°ä½ç½®ä¸€è‡´)
  let seed = 12345
  const random = () => {
    seed = (seed * 9301 + 49297) % 233280
    return seed / 233280
  }

  const starCount = Math.floor((width * height) / 15000)  // æ ¹æ®å±å¹•å¤§å°è°ƒæ•´

  ctx.fillStyle = 'rgba(255, 255, 255, 0.8)'

  for (let i = 0; i < starCount; i++) {
    const x = random() * width
    const y = random() * (height * 0.6)  // æ˜Ÿè¾°é›†ä¸­åœ¨ä¸ŠåŠéƒ¨åˆ†
    const radius = random() * 1.5

    ctx.beginPath()
    ctx.arc(x, y, radius, 0, Math.PI * 2)
    ctx.fill()

    // å¶å°”æ·»åŠ ä¸€ä¸ªå‘å…‰æ•ˆæœ
    if (random() > 0.85) {
      ctx.strokeStyle = `rgba(255, 255, 255, ${random() * 0.5})`
      ctx.lineWidth = 0.5
      ctx.beginPath()
      ctx.arc(x, y, radius * 2.5, 0, Math.PI * 2)
      ctx.stroke()
    }
  }
}

/**
 * ç»˜åˆ¶å•ä¸ªç²’å­
 * 
 * @param ctx Canvas ä¸Šä¸‹æ–‡
 * @param particle ç²’å­å¯¹è±¡
 */
export function drawParticle(
  ctx: CanvasRenderingContext2D,
  particle: Particle
): void {
  ctx.save()

  // è®¾ç½®ç²’å­çš„å¡«å……é¢œè‰²å’Œé€æ˜åº¦
  ctx.globalAlpha = particle.opacity

  // ç»˜åˆ¶ç²’å­ä¸»ä½“
  ctx.fillStyle = particle.color
  ctx.beginPath()
  ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2)
  ctx.fill()

  // æ·»åŠ ç²’å­å¤–å…‰æ™• (å¯é€‰ï¼Œå¢åŠ è§†è§‰æ•ˆæœ)
  if (particle.opacity > 0.5) {
