export const FireworksLanding: React.FC<{
  onEnter?: () => void
  autoCloseTime?: number
}> = ({ onEnter, autoCloseTime = 25000 }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null)
  const containerRef = useRef<HTMLDivElement>(null)
  const animationFrameRef = useRef<number>()
  const startTimeRef = useRef<number>(0)

  const [fireworks, setFireworks] = useState<Firework[]>([])
  const [sequence, setSequence] = useState<FireworkSequence | null>(null)
  const [holidayWish, setHolidayWish] = useState<string>('')
  const [isLoading, setIsLoading] = useState(true)
  const [canClick, setCanClick] = useState(false)

  /**
   * åˆå§‹åŒ–çƒŸèŠ±ç³»ç»Ÿ
   */
  useEffect(() => {
    if (!canvasRef.current) return

    // è·å– Canvas å°ºå¯¸
    const canvas = canvasRef.current
    const width = window.innerWidth
    const height = window.innerHeight

    // åˆå§‹åŒ– Canvas ä¸Šä¸‹æ–‡
    const { ctx, dpr } = initCanvasContext(canvas, width, height)

    // ç”ŸæˆçƒŸèŠ±åºåˆ—é…ç½®
    const seq = generateFireworkSequence(width, height, 0)
    setSequence(seq)

    // ç”ŸæˆçƒŸèŠ±åˆ—è¡¨
    const fw = generateFireworks(seq, width, height, 0)
    setFireworks(fw)

    // æ£€æµ‹èŠ‚å‡æ—¥å¹¶è·å–ç¥ç¦è¯
    const holiday = detectHoliday()
    if (holiday && fw[0]) {
      const wish = getRandomWish(holiday)
      setHolidayWish(wish)
      fw[0].holiday = {
        name: holiday.name,
        wish,
        effectType: holiday.effectType
      }
    }

    setIsLoading(false)
    startTimeRef.current = Date.now()

    // åŠ¨ç”»å¾ªç¯
    const animate = () => {
      const now = Date.now()
      const elapsed = now - startTimeRef.current

      // æ›´æ–°çƒŸèŠ±çŠ¶æ€
      updateFireworks(fw, elapsed)

      // æ¸²æŸ“ä¸€å¸§
      renderFrame(ctx, width, height, fw, elapsed, true)

      // æ£€æŸ¥æ˜¯å¦å…¨éƒ¨å®Œæˆ
      if (isSequenceFinished(fw)) {
        // ä¿æŒæ˜¾ç¤ºä¸€æ®µæ—¶é—´åå…è®¸ç‚¹å‡»
        if (elapsed > autoCloseTime) {
          setCanClick(true)
        }
      }

      animationFrameRef.current = requestAnimationFrame(animate)
    }

    animationFrameRef.current = requestAnimationFrame(animate)

    // å¤„ç†çª—å£å¤§å°æ”¹å˜
    const handleResize = () => {
      const newWidth = window.innerWidth
      const newHeight = window.innerHeight
      initCanvasContext(canvas, newWidth, newHeight)
    }

    window.addEventListener('resize', handleResize)

    // æ¸…ç†
    return () => {
      window.removeEventListener('resize', handleResize)
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current)
      }
    }
  }, [autoCloseTime])

  /**
   * å¤„ç†è¿›å…¥ç³»ç»ŸæŒ‰é’®ç‚¹å‡»
   */
  const handleEnter = () => {
    if (onEnter) {
      onEnter()
    } else {
      // å¦‚æœæ²¡æœ‰æä¾›å›è°ƒï¼Œé»˜è®¤è·³è½¬æˆ–éšè—
      if (containerRef.current) {
        containerRef.current.style.transition = 'opacity 0.5s ease'
        containerRef.current.style.opacity = '0'
        setTimeout(() => {
          if (containerRef.current) {
            containerRef.current.style.display = 'none'
          }
        }, 500)
      }
    }
  }

  /**
   * å¤„ç†å¿«é€Ÿè·³è¿‡ (å¯é€‰ï¼šæŒ‰ ESC æˆ– Enter)
   */
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === 'Enter' || e.key === 'Escape') {
        handleEnter()
      }
    }

    window.addEventListener('keydown', handleKeyDown)
    return () => window.removeEventListener('keydown', handleKeyDown)
  }, [])

  return (
    <div ref={containerRef} className="landing-container">
      {/* Canvas çƒŸèŠ±å±‚ */}
      <canvas
        ref={canvasRef}
        className="fireworks-canvas"
        style={{ background: 'transparent' }}
      />

      {/* å†…å®¹å±‚ (æ ‡é¢˜ + æŒ‰é’®) */}
      <div className="content-wrapper">
        {/* æ ‡é¢˜åŒºåŸŸ */}
        <div className="title-section">
          <h1 className="title-zh">çƒŸç«æ¼«æ¸¸</h1>
          <h2 className="title-en">Ember Glow</h2>
        </div>

        {/* æŒ‰é’®åŒºåŸŸ */}
        <div className="button-section">
          <button
            className="enter-button"
            onClick={handleEnter}
            disabled={isLoading || !canClick}
            title={canClick ? 'ç‚¹å‡»è¿›å…¥ç³»ç»Ÿ' : 'çƒŸèŠ±ç»½æ”¾ä¸­...'}
          >
            {isLoading ? 'æ­£åœ¨åŠ è½½...' : 'è¿›å…¥ç³»ç»Ÿ'}
          </button>
        </div>
      </div>

      {/* è°ƒè¯•ä¿¡æ¯ (å¼€å‘ç¯å¢ƒ) */}
      {process.env.NODE_ENV === 'development' && (
        <div
          style={{
            position: 'fixed',
            bottom: 20,
            right: 20,
            color: '#FDB927',
            fontSize: '12px',
            fontFamily: 'monospace',
            backgroundColor: 'rgba(0, 0, 0, 0.7)',
            padding: '10px',
            borderRadius: '5px',
            zIndex: 9999,
            maxWidth: '200px',
            wordBreak: 'break-all'
          }}
        >
          <div>çƒŸèŠ±æ•°: {fireworks.length}</div>
          <div>èŠ‚å‡æ—¥: {holidayWish ? 'æ˜¯' : 'å¦'}</div>
          <div>æ–‡å­—çƒŸèŠ±: {sequence?.textFireworkIndex !== -1 ? 'æœ‰' : 'æ— '}</div>
          <div>å¯ç‚¹å‡»: {canClick ? 'æ˜¯' : 'å¦'}</div>
        </div>
      )}
    </div>
  )
}

