import type { Particle } from '@/types'

/**
 * æ–‡å­—ç²’å­é…ç½®
 */
interface TextParticleConfig {
  text: string
  centerX: number
  centerY: number
  fontSize: number
  color: string
  particleDensity?: number  // ç²’å­å¯†åº¦ (1-10)
}

/**
 * ä½¿ç”¨ Canvas è·å–æ–‡å­—çš„åƒç´ ç‚¹ä½ç½®
 * è¿™æ˜¯å®ç°æ–‡å­—çƒŸèŠ±çš„æ ¸å¿ƒç®—æ³•
 */
export function getTextPixelPositions(
  text: string,
  fontSize: number = 80,
  density: number = 3
): { x: number; y: number }[] {
  // åˆ›å»ºç¦»å± Canvas
  const canvas = document.createElement('canvas')
  const ctx = canvas.getContext('2d')!

  // è®¾ç½® Canvas å°ºå¯¸ï¼ˆè¶³å¤Ÿå¤§ä»¥å®¹çº³æ–‡å­—ï¼‰
  const estimatedWidth = text.length * fontSize * 1.2
  const estimatedHeight = fontSize * 1.5
  canvas.width = estimatedWidth
  canvas.height = estimatedHeight

  // è®¾ç½®å­—ä½“æ ·å¼
  ctx.font = `bold ${fontSize}px "Noto Serif SC", "åæ–‡è¡Œæ¥·", "æ¥·ä½“", serif`
  ctx.textAlign = 'center'
  ctx.textBaseline = 'middle'
  ctx.fillStyle = '#FFFFFF'

  // ç»˜åˆ¶æ–‡å­—åˆ° Canvas
  ctx.fillText(text, estimatedWidth / 2, estimatedHeight / 2)

  // è·å–åƒç´ æ•°æ®
  const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height)
  const pixels = imageData.data

  const positions: { x: number; y: number }[] = []
  const step = Math.max(1, Math.floor(6 - density))  // é‡‡æ ·é—´éš”

  // éå†åƒç´ ï¼Œæ‰¾å‡ºéé€æ˜çš„ç‚¹
  for (let y = 0; y < canvas.height; y += step) {
    for (let x = 0; x < canvas.width; x += step) {
      const index = (y * canvas.width + x) * 4
      const alpha = pixels[index + 3]

      // å¦‚æœåƒç´ ä¸é€æ˜ï¼Œè®°å½•ä½ç½®
      if (alpha > 128) {
        // è½¬æ¢ä¸ºç›¸å¯¹äºä¸­å¿ƒçš„åæ ‡
        positions.push({
          x: x - estimatedWidth / 2,
          y: y - estimatedHeight / 2
        })
      }
    }
  }

  return positions
}

/**
 * ç”Ÿæˆæ–‡å­—çƒŸèŠ±çš„ç²’å­æ•°ç»„
 * ç²’å­ä¼šä»çˆ†ç‚¸ä¸­å¿ƒé£å‘æ–‡å­—ä½ç½®
 */
export function createTextFireworkParticles(
  config: TextParticleConfig
): Particle[] {
  const {
    text,
    centerX,
    centerY,
    fontSize,
    color,
    particleDensity = 4
  } = config

  // è·å–æ–‡å­—çš„åƒç´ ä½ç½®
  const textPositions = getTextPixelPositions(text, fontSize, particleDensity)

  const particles: Particle[] = []

  // å¤šç§é¢œè‰²ç”¨äºæ–‡å­—çƒŸèŠ±
  const colors = [
    color,
    '#FFD700',  // é‡‘è‰²
    '#FFA500',  // æ©™è‰²
    '#FF6347',  // ç•ªèŒ„çº¢
    '#FFFFFF'   // ç™½è‰²é«˜å…‰
  ]

  for (const pos of textPositions) {
    // è®¡ç®—ç›®æ ‡ä½ç½®
    const targetX = centerX + pos.x
    const targetY = centerY + pos.y

    // è®¡ç®—ä»ä¸­å¿ƒåˆ°ç›®æ ‡çš„æ–¹å‘
    const dx = targetX - centerX
    const dy = targetY - centerY
    const distance = Math.sqrt(dx * dx + dy * dy)

    // åˆå§‹é€Ÿåº¦ï¼ˆæŒ‡å‘ç›®æ ‡ä½ç½®ï¼‰
    const speed = distance * 2.5 + Math.random() * 50
    const angle = Math.atan2(dy, dx)
    const vx = Math.cos(angle) * speed
    const vy = Math.sin(angle) * speed

    // éšæœºé€‰æ‹©é¢œè‰²
    const particleColor = colors[Math.floor(Math.random() * colors.length)]

    const particle: Particle = {
      x: centerX,
      y: centerY,
      vx,
      vy,
      life: 1.2,
      color: particleColor,
      size: 2 + Math.random() * 2,
      opacity: 1,
      friction: 0.92,  // è¾ƒé«˜é˜»åŠ›ä½¿ç²’å­åœåœ¨ç›®æ ‡ä½ç½®
      trail: [{ x: centerX, y: centerY, opacity: 1 }],
      isTextParticle: true,
      targetX,
      targetY
    }

    particles.push(particle)
  }

  // æ·»åŠ ä¸€äº›è£…é¥°æ€§çš„æ•£å°„ç²’å­
  const decorCount = Math.floor(textPositions.length * 0.2)
  for (let i = 0; i < decorCount; i++) {
    const angle = Math.random() * Math.PI * 2
    const speed = 100 + Math.random() * 150

    particles.push({
      x: centerX,
      y: centerY,
      vx: Math.cos(angle) * speed,
      vy: Math.sin(angle) * speed,
      life: 0.8,
      color: colors[Math.floor(Math.random() * colors.length)],
      size: 1.5 + Math.random() * 1.5,
      opacity: 1,
      friction: 0.98,
      trail: [{ x: centerX, y: centerY, opacity: 1 }]
    })
  }

  return particles
}

/**
 * æ›´æ–°æ–‡å­—çƒŸèŠ±ç²’å­
 * æ–‡å­—ç²’å­æœ‰ç‰¹æ®Šçš„è¿åŠ¨è§„å¾‹ï¼šé£å‘ç›®æ ‡ä½ç½®ååœç•™
 */
export function updateTextParticle(particle: Particle, dt: number = 0.016): void {
  if (!particle.trail) {
    particle.trail = []
  }

  // è®°å½•è½¨è¿¹
  particle.trail.unshift({ x: particle.x, y: particle.y, opacity: particle.opacity })
  if (particle.trail.length > 8) {
    particle.trail.pop()
  }

  // æ›´æ–°è½¨è¿¹é€æ˜åº¦
  for (let i = 0; i < particle.trail.length; i++) {
    particle.trail[i].opacity = particle.opacity * (1 - i / particle.trail.length) * 0.6
  }

  if (particle.isTextParticle && particle.targetX !== undefined && particle.targetY !== undefined) {
    // æ–‡å­—ç²’å­ï¼šå‘ç›®æ ‡ä½ç½®ç§»åŠ¨
    const dx = particle.targetX - particle.x
    const dy = particle.targetY - particle.y
    const distance = Math.sqrt(dx * dx + dy * dy)

    if (distance > 5) {
      // è¿˜æ²¡åˆ°ç›®æ ‡ï¼Œç»§ç»­ç§»åŠ¨
      particle.vx *= particle.friction
      particle.vy *= particle.friction
      particle.x += particle.vx * dt
      particle.y += particle.vy * dt
    } else {
      // åˆ°è¾¾ç›®æ ‡ï¼Œåœç•™å¹¶ç¼“æ…¢æ¶ˆå¤±
      particle.x = particle.targetX
      particle.y = particle.targetY
      particle.vx = 0
      particle.vy = 0
      particle.life -= dt * 0.15  // æ…¢é€Ÿæ¶ˆå¤±
    }
  } else {
    // æ™®é€šè£…é¥°ç²’å­
    particle.vy += 60 * dt
    particle.vx *= particle.friction
    particle.vy *= particle.friction
    particle.x += particle.vx * dt
    particle.y += particle.vy * dt
    particle.life -= dt * 0.5
  }

  particle.opacity = Math.max(0, Math.pow(particle.life, 0.6))
  particle.size *= 0.999
}

/**
 * è·å–æ–‡å­—çƒŸèŠ±æ˜¾ç¤ºçš„æ¨èé…ç½®
 */
export function getTextFireworkConfig(
  text: string,
  canvasWidth: number,
  canvasHeight: number
): TextParticleConfig {
  // æ ¹æ®æ–‡å­—é•¿åº¦è°ƒæ•´å­—ä½“å¤§å°
  let fontSize = 80
  if (text.length > 6) {
    fontSize = 50
  } else if (text.length > 4) {
    fontSize = 65
  }

  // ç¡®ä¿æ–‡å­—ä¸è¶…å‡ºå±å¹•
  const maxWidth = canvasWidth * 0.8
  const estimatedWidth = text.length * fontSize * 0.8
  if (estimatedWidth > maxWidth) {
    fontSize = Math.floor(maxWidth / (text.length * 0.8))
  }

  return {
    text,
    centerX: canvasWidth / 2,
    centerY: canvasHeight * 0.4,  // ç¨å¾®åä¸Š
    fontSize,
    color: '#FFD700',
    particleDensity: 4
  }
}
