  // ç²—ç•¥ä¼°è®¡ï¼šæ¯ä¸ªå­—ç¬¦å®½åº¦ â‰ˆ å­—ä½“å¤§å° * 0.6
  const estimatedWidth = text.length * fontSize * 0.6
  return canvasWidth / 2 - estimatedWidth / 2
}

/**
