  y: number,
  count: number,
  force: number = 300,
  colors: string[] = ['#FF6B35', '#FF8C42', '#FDB927']
): Particle[] {
  const particles: Particle[] = []

  for (let i = 0; i < count; i++) {
    const angle = (i / count) * Math.PI * 2  // å‡åŒ€åˆ†å¸ƒè§’åº¦
    const speed = force * (0.5 + Math.random() * 0.5)  // éšæœºé€Ÿåº¦å˜åŒ–

    const vx = Math.cos(angle) * speed
    const vy = Math.sin(angle) * speed - 100  // å‘ä¸Šçš„åˆé€Ÿåº¦

    const color = colors[Math.floor(Math.random() * colors.length)]
    const particle = createParticle(x, y, vx, vy, color, 3 + Math.random() * 3)

    particles.push(particle)
  }

  return particles
}

/**
 * åˆ›å»ºå‡ç©ºçƒŸèŠ±çš„ç²’å­ (å‡ç©ºé˜¶æ®µ)
 * ç²’å­å½¢æˆä¸€æ¡ç«–ç›´çš„å…‰æŸ
 * 
 * @param x å‘å°„ç‚¹ X
 * @param y å‘å°„ç‚¹ Y (é€šå¸¸æ˜¯å±å¹•åº•éƒ¨)
 * @param targetY å‡ç©ºç›®æ ‡é«˜åº¦
 * @param duration å‡ç©ºæŒç»­æ—¶é•¿ (ms)
 * @param count ç²’å­æ•°é‡
 * @param color å…‰æŸé¢œè‰²
 * @returns ç²’å­æ•°ç»„
 */
export function createLaunchParticles(
  x: number,
  y: number,
  targetY: number,
  duration: number,
  count: number = 40,
  color: string = '#FFB347'
): Particle[] {
  const particles: Particle[] = []
  const distancePerSecond = Math.abs(y - targetY) / (duration / 1000)

  for (let i = 0; i < count; i++) {
    // ç²’å­æ²¿ç€ä¸Šå‡è·¯å¾„åˆ†å¸ƒ
    const offset = (i / count) * Math.abs(y - targetY)
    const px = x + (Math.random() - 0.5) * 20  // è½»å¾®çš„æ°´å¹³åå·®

    const particle = createParticle(
      px,
      y - offset,
      0,
      distancePerSecond + (Math.random() - 0.5) * 50,
      color,
      2 + Math.random() * 2
    )

    particles.push(particle)
  }

  return particles
}

/**
 * æ ¹æ®çƒŸèŠ±ç±»å‹ç”Ÿæˆå¯¹åº”çš„ç²’å­
 * è¿™æ˜¯çƒŸèŠ±ç³»ç»Ÿçš„æ ¸å¿ƒåˆ†å‘å‡½æ•°
 * 
 * @param type çƒŸèŠ±ç±»å‹
 * @param x ä½ç½® X
 * @param y ä½ç½® Y
 * @param force å¼ºåº¦
 * @param colors é¢œè‰²
 * @param particleCount ç²’å­æ•°
 * @returns ç²’å­æ•°ç»„
 */
export function generateFireworkParticles(
  type: FireworkType,
  x: number,
  y: number,
  force: number,
  colors: string[],
  particleCount: number
): Particle[] {
  switch (type) {
    case 'bloom':
      // ç»å…¸ç»½æ”¾ï¼šçƒå½¢çˆ†ç‚¸ï¼Œç²’å­å‡åŒ€åˆ†å¸ƒ
      return createExplosionParticles(x, y, particleCount, force, colors)

    case 'ring':
      // ç¯å½¢æ‰©å±•ï¼šæ²¿åœ†å½¢è½¨è¿¹çš„ç²’å­
      return createRingParticles(x, y, particleCount, force, colors)

    case 'fountain':
      // å–·æ³‰å‹ï¼šå‘ä¸Šå–·å°„ï¼Œå½¢æˆæ°´æŸ±çŠ¶
      return createFountainParticles(x, y, particleCount, force, colors)

    case 'chain':
      // é“¾å¼çˆ†ç‚¸ï¼šå¤šä¸ªçˆ†ç‚¸ç‚¹
      return createChainParticles(x, y, particleCount, force, colors)

    case 'spiral':
      // æ—‹è½¬èŠ±ç«ï¼šèºæ—‹ä¸Šå‡åçˆ†ç‚¸
      return createSpiralParticles(x, y, particleCount, force, colors)

    case 'text':
      // æ–‡å­—çƒŸèŠ±ï¼šä½¿ç”¨ç›¸åŒçš„ç²’å­ï¼Œä½†åæœŸä¼šæ˜¾ç¤ºæ–‡å­—
      return createExplosionParticles(x, y, particleCount * 0.8, force * 0.7, colors)

    default:
      return createExplosionParticles(x, y, particleCount, force, colors)
  }
}

