/**
 * ç¯å½¢æ‰©å±•çƒŸèŠ±
 * ç²’å­æ²¿ç€åœ†å½¢è·¯å¾„å‘å¤–æ‰©å±•
 */
function createRingParticles(
  x: number,
  y: number,
  count: number,
  force: number,
  colors: string[]
): Particle[] {
  const particles: Particle[] = []

  for (let i = 0; i < count; i++) {
    const angle = (i / count) * Math.PI * 2
    const speed = force * 0.8

    const vx = Math.cos(angle) * speed
    const vy = Math.sin(angle) * speed

    const color = colors[i % colors.length]
    const particle = createParticle(x, y, vx, vy, color, 4)

    particles.push(particle)
  }

  return particles
}

/**
 * å–·æ³‰å‹çƒŸèŠ±
 * ç²’å­é›†ä¸­å‘ä¸Šå–·å°„
 */
function createFountainParticles(
  x: number,
  y: number,
  count: number,
  force: number,
  colors: string[]
): Particle[] {
  const particles: Particle[] = []

  for (let i = 0; i < count; i++) {
    const angle = (Math.random() - 0.5) * Math.PI * 0.6  // é›†ä¸­åœ¨ä¸Šæ–¹
    const speed = force * (0.6 + Math.random() * 0.4)

    const vx = Math.sin(angle) * speed
    const vy = Math.cos(angle) * speed * 1.2

    const px = x + (Math.random() - 0.5) * 30
    const color = colors[Math.floor(Math.random() * colors.length)]
    const particle = createParticle(px, y, vx, vy, color, 3)

    particles.push(particle)
  }

  return particles
}

/**
 * é“¾å¼çˆ†ç‚¸çƒŸèŠ±
 * å¤šä¸ªçˆ†ç‚¸ç‚¹å„è‡ªæ”¾å°„ç²’å­
 */
function createChainParticles(
  x: number,
  y: number,
  count: number,
  force: number,
  colors: string[]
): Particle[] {
  const particles: Particle[] = []
  const chainCount = 3  // 3 ä¸ªçˆ†ç‚¸ç‚¹

  for (let chain = 0; chain < chainCount; chain++) {
    const cx = x + (chain - 1) * 100  // åˆ†æ•£çš„çˆ†ç‚¸ç‚¹
    const cy = y - chain * 50  // é€çº§ä¸Šå‡

    const particlesPerChain = Math.floor(count / chainCount)
    const chainParticles = createExplosionParticles(
      cx,
      cy,
      particlesPerChain,
      force,
      colors
    )

    particles.push(...chainParticles)
  }

  return particles
}

/**
 * æ—‹è½¬èŠ±ç«çƒŸèŠ±
 * ç²’å­èºæ—‹ä¸Šå‡åçˆ†ç‚¸
 */
function createSpiralParticles(
  x: number,
  y: number,
  count: number,
  force: number,
  colors: string[]
): Particle[] {
  const particles: Particle[] = []

  for (let i = 0; i < count; i++) {
    const angle = (i / count) * Math.PI * 4  // ä¸¤åœˆèºæ—‹
    const radius = (i / count) * 100
    const speed = force * 1.2

    const vx = Math.cos(angle) * radius * (Math.random() - 0.5) * 2 + Math.cos(angle) * speed
    const vy = Math.sin(angle) * radius * (Math.random() - 0.5) * 2 + Math.sin(angle) * speed

    const color = colors[i % colors.length]
    const particle = createParticle(x, y, vx, vy, color, 3 + Math.random() * 2)

    particles.push(particle)
  }

  return particles
}

/**
 * ç”Ÿæˆå•ä¸ªçƒŸèŠ±é…ç½®
 * 
 * @param id å”¯ä¸€ ID
 * @param startTime å¼€å§‹æ—¶åˆ»
 * @param canvasWidth Canvas å®½åº¦
 * @param canvasHeight Canvas é«˜åº¦
 * @param seed éšæœºæ•°ç§å­ (ç”¨äºå¯å¤ç°æ€§)
 * @returns çƒŸèŠ±å¯¹è±¡
 */
export function createFirework(
  id: string,
  startTime: number,
  canvasWidth: number,
  canvasHeight: number,
  seed: number = Date.now()
): Firework {
  // ä½¿ç”¨ç§å­ç”Ÿæˆä¼ªéšæœºæ•°
  const rng = (min: number, max: number) => {
    seed = (seed * 9301 + 49297) % 233280
    return min + ((seed / 233280) * (max - min))
  }

  const fireWorkTypes: FireworkType[] = ['bloom', 'ring', 'fountain', 'chain', 'spiral']
  const type = fireWorkTypes[Math.floor(rng(0, 5))] as FireworkType

  const x = rng(canvasWidth * 0.2, canvasWidth * 0.8)
  const launchHeight = rng(canvasHeight * 0.3, canvasHeight * 0.6)
  const launchDuration = rng(800, 1200)
  const explosionDuration = rng(300, 500)
  const displayDuration = rng(2000, 3000)

  const explosionTime = startTime + launchDuration + explosionDuration

  const colors = ['#FF6B35', '#FF8C42', '#FDB927', '#FFB347', '#FF69B4']
  const selectedColors = colors.slice(0, Math.floor(rng(2, 4)))

  const launchParticles = createLaunchParticles(
    x,
    canvasHeight,
    canvasHeight - launchHeight,
    launchDuration,
    30,
    selectedColors[0]
  )

  return {
    id,
    type,
    startTime,
    launchDuration,
    explosionTime,
    displayDuration,
    x,
    y: canvasHeight - launchHeight,
    launchHeight,
    explosionForce: rng(250, 350),
    particleCount: Math.floor(rng(60, 120)),
    colors: selectedColors,
    particles: launchParticles,
    isFinished: false
  }
}

