import type { Firework, FireworkSequence } from '@/types'
import { createFirework, generateFireworkParticles, updateParticle } from './fireworksEngine'
import { detectHoliday, getHolidayColorScheme } from './holidaySystem'
import { getRandomText } from './textFireworks'

/**
 * ç”Ÿæˆå®Œæ•´çš„çƒŸèŠ±åºåˆ—
 * 
 * @param canvasWidth Canvas å®½åº¦
 * @param canvasHeight Canvas é«˜åº¦
 * @param startTime åºåˆ—å¼€å§‹æ—¶åˆ» (ms)
 * @param seed éšæœºæ•°ç§å­
 * @returns çƒŸèŠ±åºåˆ—é…ç½®
 */
export function generateFireworkSequence(
  canvasWidth: number,
  canvasHeight: number,
  startTime: number = 0,
  seed: number = Date.now()
): FireworkSequence {
  // æ£€æµ‹æ˜¯å¦ä¸ºèŠ‚å‡æ—¥
  const holiday = detectHoliday()

  // ç”ŸæˆçƒŸèŠ±æ€»æ•°
  const baseFireworks = 8
  const extraFireworks = Math.floor((seed % 100) / 20)  // 0-4
  const totalFireworks = baseFireworks + extraFireworks

  // éšæœºå†³å®šæ˜¯å¦åŒ…å«æ–‡å­—çƒŸèŠ±
  const hasTextFirework = Math.random() > 0.3  // 70% çš„æ¦‚ç‡åŒ…å«æ–‡å­—çƒŸèŠ±
  const textFireworkIndex = hasTextFirework ? Math.floor(Math.random() * totalFireworks) : -1
  const displayText = hasTextFirework ? getRandomText(seed) : undefined

  return {
    totalFireworks,
    textFireworkIndex,
    displayText,
    holiday: {
      detected: holiday !== null,
      name: holiday?.name,
      effectType: holiday?.effectType
    }
  }
}

/**
 * ç”Ÿæˆå®Œæ•´çš„çƒŸèŠ±åˆ—è¡¨
 * 
 * @param sequence çƒŸèŠ±åºåˆ—é…ç½®
 * @param canvasWidth Canvas å®½åº¦
 * @param canvasHeight Canvas é«˜åº¦
 * @param startTime å¼€å§‹æ—¶åˆ»
 * @returns çƒŸèŠ±æ•°ç»„
 */
export function generateFireworks(
  sequence: FireworkSequence,
  canvasWidth: number,
  canvasHeight: number,
  startTime: number = 0
): Firework[] {
  const fireworks: Firework[] = []
  let currentTime = startTime

  for (let i = 0; i < sequence.totalFireworks; i++) {
    const firework = createFirework(
      `firework-${i}`,
      currentTime,
      canvasWidth,
      canvasHeight,
      Date.now() + i
    )

    // å¦‚æœè¿™æ˜¯æ–‡å­—çƒŸèŠ±
    if (i === sequence.textFireworkIndex && sequence.displayText) {
      firework.type = 'text'
      firework.displayText = sequence.displayText
    }

    // å¦‚æœæ˜¯èŠ‚å‡æ—¥çƒŸèŠ±
    if (sequence.holiday.detected && i === 0) {
      firework.holiday = {
        name: sequence.holiday.name || '',
        wish: '', // å°†åœ¨ Canvas ç³»ç»Ÿä¸­è®¾ç½®
        effectType: sequence.holiday.effectType || 'gold'
      }
    }

    fireworks.push(firework)

    // è®¡ç®—ä¸‹ä¸€ä¸ªçƒŸèŠ±çš„å¼€å§‹æ—¶é—´ (æœ‰é‡å ï¼Œè¥é€ è¿è´¯æ„Ÿ)
    currentTime += firework.launchDuration + firework.explosionTime - currentTime + 1200
  }

  return fireworks
}

/**
 * æ›´æ–°çƒŸèŠ±åºåˆ—ä¸­çš„æ‰€æœ‰çƒŸèŠ±
 * è´Ÿè´£çƒŸèŠ±çš„å„ä¸ªé˜¶æ®µï¼šå‡ç©º â†’ çˆ†ç‚¸ â†’ æ˜¾ç¤º
 * 
 * @param fireworks çƒŸèŠ±æ•°ç»„
 * @param currentTime å½“å‰æ—¶åˆ» (ms)
 */
export function updateFireworks(fireworks: Firework[], currentTime: number): void {
  for (const firework of fireworks) {
    if (firework.isFinished) continue

    const elapsed = currentTime - firework.startTime

    // é˜¶æ®µ1ï¼šå‡ç©º
    if (elapsed >= 0 && elapsed < firework.launchDuration) {
      // å‡ç©ºé˜¶æ®µçš„ç²’å­å·²åœ¨åˆå§‹åŒ–æ—¶ç”Ÿæˆï¼Œè¿™é‡Œå¯é€‰æ‹©æ›´æ–°å®ƒä»¬çš„ä½ç½®
      const progress = elapsed / firework.launchDuration

      for (const particle of firework.particles) {
        particle.y -= particle.vy * 0.016  // ç®€å•æ›´æ–°ä½ç½®
        particle.opacity = Math.max(0, 1 - progress)  // ç²’å­é€æ¸æ·¡å‡º
      }
    }

    // é˜¶æ®µ2ï¼šçˆ†ç‚¸
    else if (elapsed === firework.launchDuration) {
      // åœ¨çˆ†ç‚¸æ—¶åˆ»ï¼Œç”¨çˆ†ç‚¸ç²’å­æ›¿æ¢å‡ç©ºç²’å­
      firework.particles = generateFireworkParticles(
        firework.type,
        firework.x,
        firework.y,
        firework.explosionForce,
        firework.colors,
        firework.particleCount
      )
    }

    // é˜¶æ®µ3ï¼šæ˜¾ç¤º (ç²’å­æŒç»­è¿åŠ¨ï¼Œç›´è‡³æ¶ˆæ•£)
    else if (elapsed >= firework.launchDuration && elapsed < firework.launchDuration + firework.displayDuration) {
      // æ›´æ–°æ‰€æœ‰ç²’å­
      for (const particle of firework.particles) {
        updateParticle(particle)
      }

      // ç§»é™¤å·²æ¶ˆæ•£çš„ç²’å­
      firework.particles = firework.particles.filter(p => p.life > 0)

      // å¦‚æœæ‰€æœ‰ç²’å­éƒ½æ¶ˆæ•£ï¼Œæ ‡è®°çƒŸèŠ±ä¸ºå·²å®Œæˆ
      if (firework.particles.length === 0) {
        firework.isFinished = true
      }
    }

    // çƒŸèŠ±ç”Ÿå‘½å‘¨æœŸç»“æŸ
    else if (elapsed > firework.launchDuration + firework.displayDuration) {
      firework.isFinished = true
      firework.particles = []
    }
  }
}

/**
 * æ£€æŸ¥çƒŸèŠ±åºåˆ—æ˜¯å¦å…¨éƒ¨å®Œæˆ
 * 
 * @param fireworks çƒŸèŠ±æ•°ç»„
 * @returns æ˜¯å¦å…¨éƒ¨å®Œæˆ
 */
export function isSequenceFinished(fireworks: Firework[]): boolean {
  return fireworks.every(f => f.isFinished)
}

/**
 * æ£€æŸ¥çƒŸèŠ±åºåˆ—æ˜¯å¦å·²å…¨éƒ¨å¯åŠ¨
 * (ç”¨äºåˆ¤æ–­æ ‡é¢˜ä½•æ—¶å¯ä»¥å¼€å§‹æ˜¾ç¤º)
 * 
 * @param fireworks çƒŸèŠ±æ•°ç»„
 * @param currentTime å½“å‰æ—¶åˆ»
 * @returns æ˜¯å¦å…¨éƒ¨å¯åŠ¨
 */
export function isSequenceLaunched(fireworks: Firework[], currentTime: number): boolean {
  // å¦‚æœç¬¬ä¸€ä¸ªçƒŸèŠ±å·²ç»å¼€å§‹çˆ†ç‚¸ï¼Œè¯´æ˜æ•´ä¸ªåºåˆ—å·²å¯åŠ¨
  if (fireworks.length === 0) return false
  return currentTime >= fireworks[0].explosionTime
}

/**
 * ç»Ÿè®¡å½“å‰æ´»è·ƒçš„ç²’å­æ•°
 * 
 * @param fireworks çƒŸèŠ±æ•°ç»„
 * @returns ç²’å­æ€»æ•°
 */
export function getActiveParticleCount(fireworks: Firework[]): number {
  return fireworks.reduce((sum, f) => sum + f.particles.length, 0)
}

/**
 * è·å–çƒŸèŠ±åºåˆ—çš„æ€»æ—¶é•¿
 * 
 * @param fireworks çƒŸèŠ±æ•°ç»„
 * @returns æ€»æ—¶é•¿ (ms)
 */
export function getSequenceDuration(fireworks: Firework[]): number {
  if (fireworks.length === 0) return 0

  const lastFirework = fireworks[fireworks.length - 1]
  return lastFirework.startTime + lastFirework.launchDuration + lastFirework.displayDuration
}

/**
 * é‡ç½®çƒŸèŠ±åºåˆ—çŠ¶æ€
 * ç”¨äºé‡æ–°å¼€å§‹æ’­æ”¾
 * 
 * @param fireworks çƒŸèŠ±æ•°ç»„
 */
export function resetFireworks(fireworks: Firework[]): void {
  for (const firework of fireworks) {
    firework.isFinished = false
    firework.particles = []
  }
}

/**
 * è·å–æŒ‡å®šæ—¶åˆ»çš„çƒŸèŠ±ç»Ÿè®¡ä¿¡æ¯
 * ç”¨äºè°ƒè¯•å’Œæ€§èƒ½ç›‘æµ‹
 * 
 * @param fireworks çƒŸèŠ±æ•°ç»„
 * @param currentTime å½“å‰æ—¶åˆ»
 * @returns ç»Ÿè®¡ä¿¡æ¯å¯¹è±¡
 */
export function getFireworkStats(
  fireworks: Firework[],
  currentTime: number
): {
  totalFireworks: number
  finishedFireworks: number
  activeParticles: number
  textFireworkIndex: number
} {
  let textFireworkIndex = -1
  for (let i = 0; i < fireworks.length; i++) {
    if (fireworks[i].type === 'text') {
      textFireworkIndex = i
      break
    }
  }

  return {
    totalFireworks: fireworks.length,
    finishedFireworks: fireworks.filter(f => f.isFinished).length,
    activeParticles: getActiveParticleCount(fireworks),
    textFireworkIndex
  }
}

