import type { Firework, Particle } from '@/types'
import { calculateTextOpacity, generateTextFireworkConfig, getTextMetrics } from '@/utils/textFireworks'

/**
 * åˆå§‹åŒ– Canvas ä¸Šä¸‹æ–‡
 * è®¾ç½®é«˜åˆ†å±æ”¯æŒ (DPR)
 * 
 * @param canvas Canvas å…ƒç´ 
 * @param width é€»è¾‘å®½åº¦
 * @param height é€»è¾‘é«˜åº¦
 * @returns Canvas ä¸Šä¸‹æ–‡å¯¹è±¡
 */
export function initCanvasContext(
  canvas: HTMLCanvasElement,
  width: number,
  height: number
): {
  ctx: CanvasRenderingContext2D
  dpr: number
  width: number
  height: number
} {
  const dpr = window.devicePixelRatio || 1
  const ctx = canvas.getContext('2d')!

  // è®¾ç½® Canvas ç‰©ç†å°ºå¯¸
  canvas.width = width * dpr
  canvas.height = height * dpr

  // è®¾ç½® Canvas é€»è¾‘å°ºå¯¸
  canvas.style.width = `${width}px`
  canvas.style.height = `${height}px`

  // ç¼©æ”¾ä¸Šä¸‹æ–‡ä»¥é€‚åº”é«˜åˆ†å±
  ctx.scale(dpr, dpr)

  return { ctx, dpr, width, height }
}

/**
 * æ¸…ç©º Canvas èƒŒæ™¯
 * ä½¿ç”¨æ·±å¤œæ¸å˜æ•ˆæœ
 * 
 * @param ctx Canvas ä¸Šä¸‹æ–‡
 * @param width Canvas å®½åº¦
 * @param height Canvas é«˜åº¦
 */
export function clearCanvas(
  ctx: CanvasRenderingContext2D,
  width: number,
  height: number
): void {
  // åˆ›å»ºå¾„å‘æ¸å˜ (æ·±è“é»‘åˆ°çº¯é»‘)
  const gradient = ctx.createRadialGradient(
    width / 2,
    height / 2,
    0,
    width / 2,
    height / 2,
    Math.max(width, height)
  )

  gradient.addColorStop(0, '#0a0e27')  // ä¸­å¿ƒï¼šæ·±è“é»‘
  gradient.addColorStop(0.5, '#0f1419')  // ä¸­é—´è¿‡æ¸¡
  gradient.addColorStop(1, '#000000')  // å¤–åœˆï¼šçº¯é»‘

  ctx.fillStyle = gradient
  ctx.fillRect(0, 0, width, height)

  // å¯é€‰ï¼šæ·»åŠ æ˜Ÿè¾°è£…é¥°
  drawStars(ctx, width, height)
}

/**
 * ç»˜åˆ¶æ˜Ÿè¾°èƒŒæ™¯
 * ä½¿ç”¨ä¼ªéšæœºç®—æ³•ï¼Œç¡®ä¿æ¯æ¬¡åŠ è½½ä½ç½®ä¸€è‡´
 * 
 * @param ctx Canvas ä¸Šä¸‹æ–‡
 * @param width Canvas å®½åº¦
 * @param height Canvas é«˜åº¦
 */
function drawStars(ctx: CanvasRenderingContext2D, width: number, height: number): void {
  // ä¼ªéšæœºæ•°ç”Ÿæˆå™¨ (ä½¿ç”¨å›ºå®šç§å­ï¼Œæ¯æ¬¡åŠ è½½æ˜Ÿè¾°ä½ç½®ä¸€è‡´)
  let seed = 12345
  const random = () => {
    seed = (seed * 9301 + 49297) % 233280
    return seed / 233280
  }

  const starCount = Math.floor((width * height) / 15000)  // æ ¹æ®å±å¹•å¤§å°è°ƒæ•´

  ctx.fillStyle = 'rgba(255, 255, 255, 0.8)'

  for (let i = 0; i < starCount; i++) {
    const x = random() * width
    const y = random() * (height * 0.6)  // æ˜Ÿè¾°é›†ä¸­åœ¨ä¸ŠåŠéƒ¨åˆ†
    const radius = random() * 1.5

    ctx.beginPath()
    ctx.arc(x, y, radius, 0, Math.PI * 2)
    ctx.fill()

    // å¶å°”æ·»åŠ ä¸€ä¸ªå‘å…‰æ•ˆæœ
    if (random() > 0.85) {
      ctx.strokeStyle = `rgba(255, 255, 255, ${random() * 0.5})`
      ctx.lineWidth = 0.5
      ctx.beginPath()
      ctx.arc(x, y, radius * 2.5, 0, Math.PI * 2)
      ctx.stroke()
    }
  }
}

/**
 * ç»˜åˆ¶å•ä¸ªç²’å­
 * 
 * @param ctx Canvas ä¸Šä¸‹æ–‡
 * @param particle ç²’å­å¯¹è±¡
 */
export function drawParticle(
  ctx: CanvasRenderingContext2D,
  particle: Particle
): void {
  ctx.save()

  // è®¾ç½®ç²’å­çš„å¡«å……é¢œè‰²å’Œé€æ˜åº¦
  ctx.globalAlpha = particle.opacity

  // ç»˜åˆ¶ç²’å­ä¸»ä½“
  ctx.fillStyle = particle.color
  ctx.beginPath()
  ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2)
  ctx.fill()

  // æ·»åŠ ç²’å­å¤–å…‰æ™• (å¯é€‰ï¼Œå¢åŠ è§†è§‰æ•ˆæœ)
  if (particle.opacity > 0.5) {
    ctx.strokeStyle = particle.color
    ctx.lineWidth = particle.size * 0.5
    ctx.globalAlpha = particle.opacity * 0.5
    ctx.beginPath()
    ctx.arc(particle.x, particle.y, particle.size * 1.5, 0, Math.PI * 2)
    ctx.stroke()
  }

  ctx.restore()
}

/**
 * ç»˜åˆ¶æ‰€æœ‰ç²’å­
 * 
 * @param ctx Canvas ä¸Šä¸‹æ–‡
 * @param fireworks çƒŸèŠ±æ•°ç»„
 */
export function drawAllParticles(
  ctx: CanvasRenderingContext2D,
  fireworks: Firework[]
): void {
  for (const firework of fireworks) {
    for (const particle of firework.particles) {
      drawParticle(ctx, particle)
    }
  }
}

/**
 * ç»˜åˆ¶æ–‡å­—çƒŸèŠ±çš„æ–‡å­—
 * 
 * @param ctx Canvas ä¸Šä¸‹æ–‡
 * @param firework çƒŸèŠ±å¯¹è±¡
 * @param currentTime å½“å‰æ—¶åˆ»
 */
export function drawTextFirework(
  ctx: CanvasRenderingContext2D,
  firework: Firework,
  currentTime: number
): void {
  if (!firework.displayText) return

  const elapsed = currentTime - firework.startTime
  const displayStartTime = firework.launchDuration + 300  // çˆ†ç‚¸åå¼€å§‹æ˜¾ç¤º
  const displayEndTime = displayStartTime + 3000  // æ˜¾ç¤º 3 ç§’

  // æ£€æŸ¥æ˜¯å¦åœ¨æ˜¾ç¤ºæ—¶é—´å†…
  if (elapsed < displayStartTime || elapsed > displayEndTime) {
    return
  }

  const displayProgress = (elapsed - displayStartTime) / (displayEndTime - displayStartTime)
  const opacity = calculateTextOpacity(displayProgress, 0.2, 0.3)

  const config = generateTextFireworkConfig(
    firework.displayText,
    ctx.canvas.width / (window.devicePixelRatio || 1),
    ctx.canvas.height / (window.devicePixelRatio || 1)
  )

  const metrics = getTextMetrics(firework.displayText)

  ctx.save()

  // ç»˜åˆ¶æ–‡å­—é˜´å½± (æ·±çº¢è‰²ä½ç§»)
  ctx.globalAlpha = opacity * 0.6
  ctx.fillStyle = config.shadowColor
  ctx.font = config.font
  ctx.textAlign = 'left'
  ctx.fillText(
    config.text,
    config.x + 3,
    config.y + 3
  )

  // ç»˜åˆ¶ä¸»æ–‡å­— (é‡‘é»„è‰²)
  ctx.globalAlpha = opacity
  ctx.fillStyle = config.color

  // å¤šå±‚æè¾¹æ•ˆæœ
  ctx.strokeStyle = '#2C1810'
  ctx.lineWidth = 2
  ctx.strokeText(config.text, config.x, config.y)

  ctx.fillText(config.text, config.x, config.y)

  // ç»˜åˆ¶å¤–å…‰æ™•
  if (opacity > 0.7) {
    ctx.globalAlpha = opacity * 0.5
    ctx.strokeStyle = '#FF8C42'
    ctx.lineWidth = 1
    ctx.strokeText(config.text, config.x, config.y)
  }

  ctx.restore()
}

/**
 * ç»˜åˆ¶èŠ‚å‡æ—¥ç¥ç¦æ–‡å­—
 * 
 * @param ctx Canvas ä¸Šä¸‹æ–‡
 * @param firework çƒŸèŠ±å¯¹è±¡
 * @param currentTime å½“å‰æ—¶åˆ»
 */
export function drawHolidayWish(
  ctx: CanvasRenderingContext2D,
  firework: Firework,
  currentTime: number
): void {
  if (!firework.holiday) return

  const elapsed = currentTime - firework.startTime
  const displayStartTime = firework.launchDuration + 500
  const displayEndTime = displayStartTime + 4000

  if (elapsed < displayStartTime || elapsed > displayEndTime) {
    return
  }

  const displayProgress = (elapsed - displayStartTime) / (displayEndTime - displayStartTime)
  const opacity = calculateTextOpacity(displayProgress, 0.25, 0.35)

  const canvasWidth = ctx.canvas.width / (window.devicePixelRatio || 1)
  const canvasHeight = ctx.canvas.height / (window.devicePixelRatio || 1)

  ctx.save()
  ctx.globalAlpha = opacity
  ctx.fillStyle = '#FDB927'
  ctx.font = 'bold 48px æ–¹æ­£æ¥·ä½“, å®‹ä½“, serif'
  ctx.textAlign = 'center'
  ctx.textBaseline = 'middle'

  // ç»˜åˆ¶èŠ‚æ—¥ç¥ç¦æ–‡å­—
  const text = firework.holiday.wish
  ctx.strokeStyle = '#2C1810'
  ctx.lineWidth = 2
  ctx.strokeText(text, canvasWidth / 2, canvasHeight / 2)
  ctx.fillText(text, canvasWidth / 2, canvasHeight / 2)

  ctx.restore()
}

/**
 * ä¸»æ¸²æŸ“å‡½æ•°
 * ä¸€å¸§å†…å®Œæˆæ‰€æœ‰ç»˜åˆ¶
 * 
 * @param ctx Canvas ä¸Šä¸‹æ–‡
 * @param width Canvas å®½åº¦
 * @param height Canvas é«˜åº¦
 * @param fireworks çƒŸèŠ±æ•°ç»„
 * @param currentTime å½“å‰æ—¶åˆ»
 * @param showHolidayWish æ˜¯å¦æ˜¾ç¤ºèŠ‚å‡æ—¥ç¥ç¦
 */
export function renderFrame(
  ctx: CanvasRenderingContext2D,
  width: number,
  height: number,
  fireworks: Firework[],
  currentTime: number,
  showHolidayWish: boolean = true
): void {
  // 1. æ¸…ç©ºèƒŒæ™¯
  clearCanvas(ctx, width, height)

  // 2. ç»˜åˆ¶æ‰€æœ‰ç²’å­
  drawAllParticles(ctx, fireworks)

  // 3. ç»˜åˆ¶æ–‡å­—çƒŸèŠ±
  for (const firework of fireworks) {
    if (firework.type === 'text') {
      drawTextFirework(ctx, firework, currentTime)
    }
  }

  // 4. ç»˜åˆ¶èŠ‚å‡æ—¥ç¥ç¦
  if (showHolidayWish) {
    for (const firework of fireworks) {
      if (firework.holiday) {
        drawHolidayWish(ctx, firework, currentTime)
      }
    }
  }
}

