// èµ›åšçƒŸèŠ±å¯åŠ¨é¡µ - ä¸» React ç»„ä»¶
// ============================================================================

import React, { useEffect, useRef, useState } from 'react'
import './styles.css'

import { generateFireworkSequence, generateFireworks, updateFireworks, isSequenceFinished, getSequenceDuration } from '@/utils/fireworksSequence'
import { renderFrame, initCanvasContext } from '@/utils/canvasRenderer'
import { detectHoliday, getRandomWish } from '@/utils/holidaySystem'
import type { Firework, FireworkSequence } from '@/types'

/**
 * èµ›åšçƒŸèŠ±å¯åŠ¨é¡µç»„ä»¶
 * 
 * åŠŸèƒ½ï¼š
 * 1. Canvas çƒŸèŠ±åŠ¨ç”»æ¸²æŸ“
 * 2. èŠ‚å‡æ—¥è‡ªåŠ¨æ£€æµ‹ä¸ç¥ç¦æ˜¾ç¤º
 * 3. åŠ¨ç”»æ ‡é¢˜ (ä¸­æ–‡ã€è‹±æ–‡)
 * 4. è¿›å…¥ç³»ç»ŸæŒ‰é’®
 * 5. æ–‡å­—çƒŸèŠ±éšæœºæ˜¾ç¤º
 */
export const FireworksLanding: React.FC<{
  onEnter?: () => void
  autoCloseTime?: number
}> = ({ onEnter, autoCloseTime = 25000 }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null)
  const containerRef = useRef<HTMLDivElement>(null)
  const animationFrameRef = useRef<number>()
  const startTimeRef = useRef<number>(0)

  const [fireworks, setFireworks] = useState<Firework[]>([])
  const [sequence, setSequence] = useState<FireworkSequence | null>(null)
  const [holidayWish, setHolidayWish] = useState<string>('')
  const [isLoading, setIsLoading] = useState(true)
  const [canClick, setCanClick] = useState(false)

  /**
   * åˆå§‹åŒ–çƒŸèŠ±ç³»ç»Ÿ
   */
  useEffect(() => {
    if (!canvasRef.current) return

    // è·å– Canvas å°ºå¯¸
    const canvas = canvasRef.current
    const width = window.innerWidth
    const height = window.innerHeight

    // åˆå§‹åŒ– Canvas ä¸Šä¸‹æ–‡
    const { ctx, dpr } = initCanvasContext(canvas, width, height)

    // ç”ŸæˆçƒŸèŠ±åºåˆ—é…ç½®
    const seq = generateFireworkSequence(width, height, 0)
    setSequence(seq)

    // ç”ŸæˆçƒŸèŠ±åˆ—è¡¨
    const fw = generateFireworks(seq, width, height, 0)
    setFireworks(fw)

    // æ£€æµ‹èŠ‚å‡æ—¥å¹¶è·å–ç¥ç¦è¯
    const holiday = detectHoliday()
    if (holiday && fw[0]) {
      const wish = getRandomWish(holiday)
      setHolidayWish(wish)
      fw[0].holiday = {
        name: holiday.name,
        wish,
        effectType: holiday.effectType
      }
    }

    setIsLoading(false)
    startTimeRef.current = Date.now()

    // åŠ¨ç”»å¾ªç¯
    const animate = () => {
      const now = Date.now()
      const elapsed = now - startTimeRef.current

      // æ›´æ–°çƒŸèŠ±çŠ¶æ€
      updateFireworks(fw, elapsed)

      // æ¸²æŸ“ä¸€å¸§
      renderFrame(ctx, width, height, fw, elapsed, true)

      // æ£€æŸ¥æ˜¯å¦å…¨éƒ¨å®Œæˆ
      if (isSequenceFinished(fw)) {
        // ä¿æŒæ˜¾ç¤ºä¸€æ®µæ—¶é—´åå…è®¸ç‚¹å‡»
        if (elapsed > autoCloseTime) {
          setCanClick(true)
        }
      }

      animationFrameRef.current = requestAnimationFrame(animate)
    }

    animationFrameRef.current = requestAnimationFrame(animate)

    // å¤„ç†çª—å£å¤§å°æ”¹å˜
    const handleResize = () => {
      const newWidth = window.innerWidth
      const newHeight = window.innerHeight
      initCanvasContext(canvas, newWidth, newHeight)
    }

    window.addEventListener('resize', handleResize)

    // æ¸…ç†
    return () => {
      window.removeEventListener('resize', handleResize)
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current)
      }
    }
  }, [autoCloseTime])

  /**
   * å¤„ç†è¿›å…¥ç³»ç»ŸæŒ‰é’®ç‚¹å‡»
   */
  const handleEnter = () => {
    if (onEnter) {
      onEnter()
    } else {
