  // ä½¿ç”¨å†œå†è½¬é˜³å†ç®—æ³•

  if (year < 1900 || year > 2100) {
    console.warn(`ä¸æ”¯æŒçš„å¹´ä»½: ${year}ï¼Œä½¿ç”¨é»˜è®¤å€¼`)
    return { month: 2, day: 10 }
  }

  const index = year - 1900
  const info = LUNAR_DATA[index]

  // è§£æä¿¡æ¯
  const isLeap = (info >> 16) & 0x0f
  const leap = (info >> 16) & 0x0f
  const offset = (info >> 5) & 0x3ff

  // è®¡ç®—æ˜¥èŠ‚æ—¥æœŸï¼ˆä»ä¸Šä¸€å¹´11æœˆåˆä¸€å¼€å§‹è®¡æ•°ï¼‰
  let days = offset
