 * é™¤å¤• = æ˜¥èŠ‚å‰ä¸€å¤©
 */
export function getChusXiDate(year: number): { month: number; day: number } {
  const springFestival = getChineseNewYearDate(year)

  if (springFestival.day === 1) {
    // å¦‚æœæ˜¥èŠ‚æ˜¯ 1 æœˆ 1 æ—¥ï¼ˆä¸å¯èƒ½ï¼‰ï¼Œåˆ™å¾€å‰æ¨åˆ°ä¸Šä¸€å¹´ 12 æœˆ 31 æ—¥
    return { month: 12, day: 31 }
  } else if (springFestival.month === 1 && springFestival.day === 1) {
    return { month: 12, day: 31 }
  } else if (springFestival.day > 1) {
    // æ­£å¸¸æƒ…å†µï¼šæ˜¥èŠ‚å¾€å‰æ¨ä¸€å¤©
    return { month: springFestival.month, day: springFestival.day - 1 }
  } else {
    // å¦‚æœæ˜¥èŠ‚æ˜¯æŸæœˆçš„ç¬¬ 1 å¤©ï¼Œå¾€å‰æ¨åˆ°ä¸Šä¸ªæœˆæœ€åä¸€å¤©
    const prevMonth = springFestival.month - 1
    const daysInPrevMonth = MONTH_DAYS[prevMonth - 1] || 30
    const isLeap = isLeapYear(year)
    const actualDays = prevMonth === 2 && isLeap ? 29 : daysInPrevMonth

    return { month: prevMonth, day: actualDays }
  }
}

