/**
 * æ£€æµ‹å½“å‰æ˜¯å¦ä¸ºèŠ‚æ—¥
 * @returns èŠ‚æ—¥ä¿¡æ¯æˆ–null
 */
export function detectHoliday(date: Date = new Date()): Holiday | null {
  const month = date.getMonth() + 1
  const day = date.getDate()
  const year = date.getFullYear()
  const solarKey = `${month}-${day}`

  // å…ˆæ£€æŸ¥å…¬å†èŠ‚æ—¥
  if (HOLIDAYS[solarKey] && !HOLIDAYS[solarKey].isLunar) {
    return HOLIDAYS[solarKey]
  }

  // æ£€æŸ¥å†œå†èŠ‚æ—¥
  const yearLunarMap = LUNAR_TO_SOLAR[year]
  if (yearLunarMap) {
    for (const [lunarKey, solarDate] of Object.entries(yearLunarMap)) {
      if (solarDate === solarKey && HOLIDAYS[lunarKey]) {
        return HOLIDAYS[lunarKey]
      }
    }
  }

  return null
}

