// è®°å½•å½“å‰ä½ç½®åˆ°è½¨è¿¹
if (!particle.trail) {
  particle.trail = []
}

// æ·»åŠ å½“å‰ä½ç½®åˆ°è½¨è¿¹å¤´éƒ¨
particle.trail.unshift({ x: particle.x, y: particle.y, opacity: particle.opacity })

// é™åˆ¶è½¨è¿¹é•¿åº¦
if (particle.trail.length > MAX_TRAIL_LENGTH) {
  particle.trail.pop()
}

// æ›´æ–°è½¨è¿¹ç‚¹çš„é€æ˜åº¦ï¼ˆé€æ¸æ¶ˆå¤±ï¼‰
for (let i = 0; i < particle.trail.length; i++) {
  particle.trail[i].opacity = particle.opacity * (1 - i / particle.trail.length) * 0.8
}

// ===== ç»˜åˆ¶è½¨è¿¹çº¿æ¡ =====
if (trail && trail.length > 1) {
  // ä½¿ç”¨è´å¡å°”æ›²çº¿ç»˜åˆ¶å¹³æ»‘è½¨è¿¹
  ctx.beginPath()
  ctx.moveTo(x, y)

  for (let i = 1; i < trail.length; i++) {
    const point = trail[i]
    const prevPoint = trail[i - 1]

    // ä½¿ç”¨äºŒæ¬¡è´å¡å°”æ›²çº¿ä½¿è½¨è¿¹æ›´å¹³æ»‘
    const cpX = (prevPoint.x + point.x) / 2
    const cpY = (prevPoint.y + point.y) / 2
    ctx.quadraticCurveTo(prevPoint.x, prevPoint.y, cpX, cpY)
  }

  // åˆ›å»ºè½¨è¿¹æ¸å˜
  const lastPoint = trail[trail.length - 1]
  const gradient = ctx.createLinearGradient(x, y, lastPoint.x, lastPoint.y)
  gradient.addColorStop(0, color)
  gradient.addColorStop(0.3, `${color}CC`)
  gradient.addColorStop(0.6, `${color}66`)
  gradient.addColorStop(1, 'transparent')

  ctx.strokeStyle = gradient
  ctx.lineWidth = Math.max(1, size * 0.8)
  ctx.lineCap = 'round'
  ctx.globalAlpha = opacity
  ctx.stroke()
}

